import React, { Component } from 'react';
import Screen from './Screen';

const gridValues = ["1", "2", "3", "4", "5", "6", "7", "8", "9", "0", "."];
const operators = ["+", "-", "/", "*", "=", ".", "%", "C", "<"];

function GridButton(props) {
    return (
        <button className="gridButton">
            {props.value}
        </button>
    )
}

class Grid extends Component {
    constructor(props) {
        super(props)
        this.state = {activeButton: props.activeButton}
    }



    render() {
        const gridButtons = gridValues.map((btn) =>
            <GridButton key={btn.toString()}
                        value={btn} />
        );

        return (
            <div>
                <Screen/>
                <ul>
                    {gridButtons}
                </ul>
            </div>
        )
    }
}

export default Grid;
